drop table if exists personas;
create table personas (
  id text primary key,
  active int default 0, 
  name text not null,
  email text,
  private text not null,
  public text not null
);

drop table if exists stars;
create table stars (
  id text primary key,
  creator text not null,
  text text not null
);

